command:example.dev:code:
PS C:\> $profileName = "Web Server 10"
PS C:\> $svr         = Get-HPOVServer "Encl1, Bay 1" -ApplianceConnection 'MyAppliance.domain.com'
PS C:\> $con1        = Get-HPOVNetwork "Net-41-A"  -ApplianceConnection 'MyAppliance.domain.com' | New-HPOVServerProfileConnection -connectionId 1
PS C:\> $con2        = Get-HPOVNetwork "Net-41-B" -ApplianceConnection 'MyAppliance.domain.com' | New-HPOVServerProfileConnection -connectionId 2
PS C:\> $params = @{
>>    name                = $profileName;
>>    assignmentType      = "server";
>>    server              = $svr;
>>    connections         = ($con1, $con2);
>>    ApplianceConnection = 'MyAppliance.domain.com'
>>    }
>>    
PS C:\> New-HPOVServerProfile @params | Wait-HPOVTaskComplete

PS C:\> $profileName = 'Hypervisor Cluster Node 1'
PS C:\> $server = Get-HPOVServer "Encl1, Bay 1" #Gen9 Server
PS C:\> $con1 = Get-HPOVNetwork "Net-41-A" -ApplianceConnection 'MyAppliance.domain.com' | New-HPOVServerProfileConnection -connectionId 1
PS C:\> $con2 = Get-HPOVNetwork "Net-41-B" -ApplianceConnection 'MyAppliance.domain.com' | New-HPOVServerProfileConnection -connectionId 2
PS C:\> $LogicalDisk = New-HPOVServerProfileLogicalDisk -Name 'My Local Disk' -RAID RAID1 -NumberofDrives 2 -DiskType SasSsd
PS C:\> $params = @{
>>    name                = $profileName;
>>    assignmentType      = "server"
>>    server              = $server;
>>    connections         = $con1, $con2
>>    manageboot          = $True;
>>    bootMode            = "UEFI";
>>    pxeBootPolicy       = "IPv4ThenIPv6";
>>    bootOrder           = "HardDisk";
>>    HideUnusedFlexnics  = $True;
>>    LocalStorage        = $True;
>>    Initialize          = $True;
>>    LogicalDisk         = $LogicalDisk;
>>    ApplianceConnection = 'MyAppliance.domain.com'
>>    }
>>    
PS C:\> New-HPOVServerProfile @params | Wait-HPOVTaskComplete

