>>    serverHardwareType  = $bl460SHT;
>>    enclosureGroup      = $eg;
>>    connections         = $con1, $con2;
>>    ApplianceConnection = 'MyAppliance1.domain.com'
>>}
>>
PS C:\> New-HPOVServerProfileTemplate @params | Wait-HPOVTaskComplete

PS C:\> $profileName = "SQL DB Cluster Node Template v1.0"
