PS C:\> $bl660bios = @(@{id=210;value=3},@{id=140;value=3})
PS C:\> $enclosuregroup = Get-HPOVEnclosureGroup "Production EG1"
PS C:\> $con1 = Get-HPOVNetwork "Net 41-A" | New-HPOVServerProfileConnection -connectionId 1
PS C:\>$con2 = Get-HPOVNetwork "Net 41-B" | New-HPOVServerProfileConnection -connectionId 2
PS C:\> $con3 = Get-HPOVNetwork "fabric a" | New-HPOVServerProfileConnection -id 3 -type FibreChannel -bootable -priority Primary -arrayWWPn"21:11:00:02:AC:00:72:03" -LUN 0
PS C:\> $con4 = Get-HPOVNetwork "fabric b" | New-HPOVServerProfileConnection -id 4 -type FibreChannel -bootable -priority Secondary -arrayWWPn "22:12:00:02:AC:00:72:03" -LUN 0
PS C:\> $LogicalDisk = New-HPOVServerProfileLogicalDisk -Name "My Local Disk" -RAID RAID1 -NumberofDrives 2 -DiskType SasSsd 
PS C:\> $volume1 = Get-HPOVStorageVolume SharedVolume1 | New-HPOVServerProfileAttachVolume
PS C:\> $params = @{
>>    name                = $profileName;
>>    connections         = $con1, $con2, $con3, $con4;
